mapper.select:
    select

    from call_record
	where user_info_id = #{callRecordDTO.userId}

      and call_result = #{callRecordDTO.callResult}


      and language_trick_id = #{callRecordDTO.languageTrickId}


      and call_time >= #{callRecordDTO.startTime}


      and call_time <= #{callRecordDTO.endTime}

    ORDER BY create_time DESC
    limit #{pageItemStart},#{callRecordDTO.pageSize}

    select count(*) from call_record
	where user_info_id = #{callRecordDTO.userId}

      and call_result = #{callRecordDTO.callResult}


      and language_trick_id = #{callRecordDTO.languageTrickId}


      and call_time >= #{callRecordDTO.startTime}


      and call_time <= #{callRecordDTO.endTime}
